// Spotify.js
// Handles Spotify API search for tracks

const token = 'YOUR_SPOTIFY_TOKEN_HERE'; // Replace with a valid token

export async function searchTracks(query) {
  const endpoint = `https://api.spotify.com/v1/search?q=${encodeURIComponent(query)}&type=track&limit=10`;
  const response = await fetch(endpoint, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
  const data = await response.json();
  if (!data.tracks || !data.tracks.items) return [];
  return data.tracks.items.map(track => ({
    id: track.id,
    name: track.name,
    artists: track.artists.map(a => a.name),
    album: track.album.name,
  }));
}
