      { name: "x", artist: "x", album: "x" },
      { name: "x", artist: "x", album: "x" },
      { name: "x", artist: "x", album: "x" }
    ]);
  function addTrack() {
    setTracks([
      ...tracks, 
      { name: "bad ones", artist: "Tate Mcrae", album: "Too Young to Be Sad" }
    ]);
  }

  return (
    <div className="Playlist">
      <h1>Playlist</h1>
      <div className="Tracklist">
        {tracks.map((track, index) => (
          <div key={index} className="Track" onClick={addTrack}>
            <h4 className="SongName">
              {track.name} <span className="type">{type ? "+": "-"}</span>
            </h4>
            <p className="SongInfo">
              {track.artist} | {track.album}
            </p>
          </div>
        ))}
        {/*TopTracks()*/}
      </div>
    </div>
  );
}


export default (Playlist);
