//import TopTracks from "./Song";
import { useState } from "react";
import Tracklist from "./Tracklist";

const type = false;

function Playlist() {
  const [playlist, setPlaylist] = useState([
      { name: "bad ones", artists: "Tate Mcrae", album: "Too Young to Be Sad" }
  ]);


  function addTrackToPlaylist(track) {
    setPlaylist([
      ...playlist, 
      track
    ]);
  }

  return (
    <div className="Playlist">
      <h1>Playlist</h1>
      <div className="Tracklist">
        {playlist.map((track, index) => (
          <div key={index} songName={track.name} className="Track">
            <h4 className="SongName">
              {track.name} <span className="type">{type ? "+": "-"}</span>
            </h4>
            <p className="SongInfo">
              {track.artists} | {track.album}
            </p>
          </div>
        ))}
        {/*TopTracks()*/}
      </div>

    </div>
  );
}




export default (Playlist) ;
