const type = false;

function Playlist( { playlist, onRemoveTrack } ) {
  return (
    <div className="Playlist">
      <form>
        <input type="text" name="save-to-spotify" placeholder="Playlist"></input>
        <input name="d"></input>
        <div className="Tracklist">
          {playlist.map((track) => (
            <div key={track.index} className="Track">
              <h4 className="SongName">
                <a target="_blank" href={"https://open.spotify.com/track/" + track.id} >{track.name}</a> <span onClick={() => onRemoveTrack(track.index)} className="type">{type ? "+": "-"}</span>
              </h4>
              <p className="SongInfo">
                {track.artists} | {track.album}
              </p>
            </div>
          ))}
        </div>
        <input type="submit" value="SAVE TO SPOTIFY" className="saveToSpotify"></input>
      </form>
    </div>
  );
}




export default (Playlist) ;
