import React, { useState, useEffect } from 'react';
import '../styles/App.css';
import '../styles/Header.css';
import SearchBar from './SearchBar';
import Playlist from './Playlist';
import Tracklist from './Tracklist';

function App() {
  const [playlist, setPlaylist] = useState([
    { index: 1, name: "bad ones", artists: ["Tate Mcrae"], album: "Too Young to Be Sad", id: "6CYT0souHUHKTO4EMzTUFg" }
  ]);
  const [playlistName, setPlaylistName] = useState("Baddest Motherfucker");
  const [searchResults, setSearchResults] = useState([
    { name: "Blinding Lights", artists: ["The Weeknd"], album: "After Hours", id: "1" },
    { name: "Levitating", artists: ["Dua Lipa"], album: "Future Nostalgia", id: "2" },
    { name: "Peaches", artists: ["Justin Bieber"], album: "Justice", id: "3" }
  ]);
  const [searchTerm, setSearchTerm] = useState('');
  const clientId = 'de8c5edcc39e4f3da974e2e22fe51915';
  const redirectUri = window.location.origin;
  const [token, setToken] = useState(null);

  // Parse search term from URL (?search=...)
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const term = params.get('search');
    if (term) setSearchTerm(term);
  }, []);

  function getAccessToken() {
    if (token) return token;
    const hash = window.location.hash;
    if (hash) {
      const params = new URLSearchParams(hash.substring(1));
      const accessToken = params.get('access_token');
      if (accessToken) {
        setToken(accessToken);
        window.location.hash = '';
        return accessToken;
      }
    }
    const authUrl = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&scope=user-read-private&redirect_uri=${encodeURIComponent(redirectUri)}`;
    window.location = authUrl;
  }

  async function fetchSpotifyTracks(query) {
    const accessToken = token || getAccessToken();
    if (!accessToken) return;
    const q = encodeURIComponent(query || 'pop');
    const response = await fetch(`https://api.spotify.com/v1/search?q=${q}&type=track&limit=5`, {
      headers: {
        Authorization: `Bearer ${accessToken}`,
      }
    });
    const data = await response.json();
    if (data.tracks && data.tracks.items) {
      setSearchResults(
        data.tracks.items.map(track => ({
          id: track.id,
          name: track.name,
          artists: track.artists.map(a => a.name),
          album: track.album.name
        }))
      );
    }
  }

  // Fetch tracks when searchTerm changes
  useEffect(() => {
    if (searchTerm) {
      fetchSpotifyTracks(searchTerm);
    }
    // eslint-disable-next-line
  }, [searchTerm]);

  function addTrackToPlaylist(track) {
    setPlaylist((prevPlaylist) => [...prevPlaylist, {...track, index: prevPlaylist.length + 1}]);
  }

  const handlePlaylistNameChange = (event) => {
    setPlaylistName(event.target.value);
  };

  function removeTrackFromPlaylist(index) {
    setPlaylist((prevPlaylist) => {
      const updatedPlaylist = prevPlaylist.filter((track) => track.index !== index);

      return updatedPlaylist.map((track, index) => ({
        ...track,
        index: index + 1, 
      }));
    });
  }

  // Handler to be passed to SearchBar
  function handleSearch(term) {
    setSearchTerm(term);
    fetchSpotifyTracks(term);
  }

  return (
    <div className="all">
      <div className="App">
        <header className="App-header">
          <h1>Ja<span className="purple">mmm</span>ing</h1>
        </header>
      </div>
      <SearchBar onSearch={handleSearch} />
      <div className='flex'>
        <div className="SearchResults">
          <h1>Results</h1>
          <Tracklist tracks={searchResults} onAddTrack={addTrackToPlaylist} />
        </div>
        <Playlist playlist={playlist} onRemoveTrack={removeTrackFromPlaylist} playlistName={playlistName} handlePlaylistNameChange={handlePlaylistNameChange} />
      </div>
      <p>Jeg vil gerne have en knap, hvor man kan se mine mest lyttede sange+kunstnere i hver måned(5)+år(10)</p>
    </div>
  );
}

export default App;
