// Authorization token that must have been created previously. See : https://developer.spotify.com/documentation/web-api/concepts/authorization
const token = 'BQC7Jx3-qEidcgeyIxDXoGStBz3kUyCQQUQ5hJnvp0hYlrMjVw6WzC9DFC-Te2nUbKpNvrZDaNkNuEo72dObWHUuObUyo6ahg6lsWMv8kmVCTkzucP42aEFKyER44tvbmkogGL4L5dhmJotkSzzizPJHoKsZLL2P8Oa90S6iH6GE37ZtLRet7GC8hVm6e_bGl3ZueFR_FQl54EVXQdbxwjwUdTWWCM7YqeFAzsJt_gcx_x4RMmRfq6T13cS5r3qrcEPrXRD0hA1vn7-LmFEqFLlbqe3fVarWcYuISfaVcEChGrkqrc6ILrRp';
async function fetchWebApi(endpoint, method, body) {
  const res = await fetch(`https://api.spotify.com/${endpoint}`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
    method,
    body:JSON.stringify(body)
  });
  return await res.json();
}

async function getTopTracks(){
  // Endpoint reference : https://developer.spotify.com/documentation/web-api/reference/get-users-top-artists-and-tracks
  return (await fetchWebApi(
    'v1/me/top/tracks?time_range=long_term&limit=5', 'GET'
  )).items;
}

const topTracks = await getTopTracks();
function TopTracks() {
  const arr = [];
  topTracks?.map(
    ({name, artists, album}) =>
      arr.push([name, artists, album])
  )
  return (
    <div>
      {Track(arr[0][0], arr[0][0], arr[0][0])}
    </div>
  )
}

export default TopTracks;
