//import TopTracks from "./Song";
import { useState } from "react";

const type = false;

function Playlist() {
  const [tracks, setTracks] = useState([
      { name: "bad ones", artists: "Tate Mcrae", album: "Too Young to Be Sad" }
  ]);


  function addTrack(e) {
    setTracks([
      ...tracks, 
      { name: e.target.parentElement.parentElement.getAttribute('songName'), artists: ["Tate Mcrae"], album: "Too Young to Be Sad" }
    ]);
  }

  return (
    <div className="Playlist">
      <h1>Playlist</h1>
      <div className="Tracklist">
        {tracks.map((track, index) => (
          <div key={index} className="Track">
            <h4 className="SongName">
              {track.name} <span className="type">{type ? "+": "-"}</span>
            </h4>
            <p className="SongInfo">
              {track.artists} | {track.album}
            </p>
          </div>
        ))}
        {/*TopTracks()*/}
      </div>
    </div>
  );
}




export default (Playlist) ;
