import Track from "./Track";
import TopTracks from "./Song";
import { useState } from "react";

const type = false;

function Playlist() {
  const [tracks, setTracks] = useState([
    { songName: "wish i loved you in the 90s", singer: "Tate McRae", album: "Too Young to Be Sad" },
  ]);

  function AddToPlaylist() {
    setTracks([
      ...tracks,
      { songName: "bad ones", singer: "Tate McRae", album: "Too Young to Be" },
    ]);
  }

  return (
    <div className="Playlist">
      <h1>Playlist</h1>
      <div className="Tracklist">
        {tracks.map((track, index) => (
          <Track
            key={index}
            songName={track.songName}
            singer={track.singer}
            album={track.album}
            type={type}
          />
        ))}
        {TopTracks()}
      </div>
      <button onClick={AddToPlaylist}>Add Song</button>
    </div>
  );
}


export default (Playlist);
