import React, { useEffect, useState } from 'react';
import { getTopArtists, getTopTracks, getTopAlbums } from './Spotify';

export default function Top() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [artists, setArtists] = useState([]);
  const [tracks, setTracks] = useState([]);
  const [albums, setAlbums] = useState([]);

  useEffect(() => {
    let isMounted = true;
    async function load() {
      try {
        setLoading(true);
        setError('');
        const [a, t, al] = await Promise.all([
          getTopArtists(10),
          getTopTracks(10),
          getTopAlbums(10)
        ]);
        if (!isMounted) return;
        setArtists(a);
        setTracks(t);
        setAlbums(al);
      } catch (e) {
        if (!isMounted) return;
        setError(e.message || 'Failed to load top items');
      } finally {
        if (isMounted) setLoading(false);
      }
    }
    load();
    return () => { isMounted = false; };
  }, []);

  return (
    <div style={{maxWidth: 1000, margin: '2rem auto', background: '#fff', borderRadius: 16, boxShadow: '0 4px 24px rgba(68,22,68,0.12)', padding: '2rem'}}>
      <h1 style={{textAlign:'center', color:'rgb(68,22,68)', marginBottom: '2rem'}}>Your Top 10 (All Time)</h1>
      {loading && <p>Loading...</p>}
      {error && <p style={{color:'red'}}>Error: {error}</p>}
      {!loading && !error && (
        <div style={{display:'grid', gridTemplateColumns:'repeat(3, 1fr)', gap:'1.5rem'}}>
          <section>
            <h2 style={{color:'rgb(68,22,68)'}}>Artists</h2>
            <ol style={{paddingLeft: '1.25rem'}}>
              {artists.map((a, i) => (
                <li key={a.id} style={{marginBottom: 12, display:'flex', alignItems:'center', gap:12}}>
                  <img src={a.images?.[0]?.url} alt={a.name} width={56} height={56} style={{borderRadius: '50%', objectFit:'cover'}} />
                  <div>
                    <div style={{fontWeight: 600}}>{a.name}</div>
                    <div style={{color:'#888', fontSize: 13}}>{a.genres?.slice(0,2).join(', ')}</div>
                  </div>
                </li>
              ))}
            </ol>
          </section>
          <section>
            <h2 style={{color:'rgb(68,22,68)'}}>Songs</h2>
            <ol style={{paddingLeft: '1.25rem'}}>
              {tracks.map((t) => (
                <li key={t.id} style={{marginBottom: 12, display:'flex', alignItems:'center', gap:12}}>
                  <img src={t.album?.images?.[0]?.url} alt={t.name} width={56} height={56} style={{borderRadius: 8, objectFit:'cover'}} />
                  <div>
                    <div style={{fontWeight: 600}}>{t.name}</div>
                    <div style={{color:'#888', fontSize: 13}}>{t.artists?.map(a=>a.name).join(', ')}</div>
                  </div>
                </li>
              ))}
            </ol>
          </section>
          <section>
            <h2 style={{color:'rgb(68,22,68)'}}>Albums</h2>
            <ol style={{paddingLeft: '1.25rem'}}>
              {albums.map((al) => (
                <li key={al.id} style={{marginBottom: 12, display:'flex', alignItems:'center', gap:12}}>
                  <img src={al.images?.[0]?.url} alt={al.name} width={56} height={56} style={{borderRadius: 8, objectFit:'cover'}} />
                  <div>
                    <div style={{fontWeight: 600}}>{al.name}</div>
                    <div style={{color:'#888', fontSize: 13}}>{al.artists?.map(a=>a.name).join(', ')}</div>
                  </div>
                </li>
              ))}
            </ol>
          </section>
        </div>
      )}
    </div>
  );
}
