import '../styles/App.css';
import '../styles/Header.css';
import SearchBar from './SearchBar';
import Playlist from './Playlist';
import Tracklist from './Tracklist';
import { useState } from 'react';
import { searchTracks, savePlaylistToSpotify } from './Spotify';

function App() {
  const [playlist, setPlaylist] = useState([
    { index: 1, name: "bad ones", artists: ["Tate Mcrae"], album: "Too Young to Be Sad", id: "6CYT0souHUHKTO4EMzTUFg", uri: "spotify:track:6CYT0souHUHKTO4EMzTUFg" }
  ]);
  const [playlistName, setPlaylistName] = useState("Baddest Motherfucker");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  function addTrackToPlaylist(track) {
    setPlaylist((prevPlaylist) => [...prevPlaylist, { ...track, index: prevPlaylist.length + 1, uri: track.uri || `spotify:track:${track.id}` }]);
  }

  const handlePlaylistNameChange = (event) => {
    setPlaylistName(event.target.value);
  };

  function removeTrackFromPlaylist(index) {
    setPlaylist((prevPlaylist) => {
      const updatedPlaylist = prevPlaylist.filter((track) => track.index !== index);
      return updatedPlaylist.map((track, index) => ({
        ...track,
        index: index + 1,
      }));
    });
  }

  async function handleSearch(query) {
    setLoading(true);
    const results = await searchTracks(query);
    setSearchResults(results);
    setLoading(false);
  }

  async function handleSavePlaylist() {
    setSaving(true);
    const uris = playlist.map(track => track.uri);
    try {
      await savePlaylistToSpotify(playlistName, uris);
      alert('Playlist saved to your Spotify account!');
      setPlaylist([]);
    } catch (e) {
      alert('Failed to save playlist. Please try again.');
    }
    setSaving(false);
  }

  function hasDuplicateTracks() {
    const seen = new Set();
    for (const track of playlist) {
      if (seen.has(track.id)) return true;
      seen.add(track.id);
    }
    return false;
  }

  return (
    <div className="all">
      {/* Show warning if there are duplicate tracks */}
      {hasDuplicateTracks() && (
        <div style={{background:'#ffeaea',color:'#a00',padding:'0.75rem 1rem',borderRadius:8,margin:'1rem 0',fontWeight:'bold',textAlign:'center'}}>
          Warning: There are duplicate songs in your playlist!
        </div>
      )}
      <SearchBar onSearch={handleSearch} />
      <div className='flex'>
        <div className="SearchResults">
          <h1>Results</h1>
          {loading ? <p>Loading...</p> : <Tracklist tracks={searchResults} onAddTrack={addTrackToPlaylist} />}
        </div>
        <Playlist playlist={playlist} onRemoveTrack={removeTrackFromPlaylist} playlistName={playlistName} handlePlaylistNameChange={handlePlaylistNameChange} onSavePlaylist={handleSavePlaylist} />
      </div>
      {saving && <p>Saving playlist to Spotify...</p>}
      <p>Jeg vil gerne have en knap, hvor man kan se mine mest lyttede sange+kunstnere i hver måned(5)+år(10)</p>
    </div>
  );
}

export default App;
